function twmp_get_flexible_content_data($array)
{
	if (!function_exists('get_sub_field')) {
		return;
	}

	$items = [];

	foreach ($array as $key => $field_key) {
		$items[$key] = get_sub_field($field_key);
	}

	return $items;
}

function twmp_get_post_query($post_args)
{
	$default_post_args = [
		'post_status' => 'publish',
		'update_post_meta_cache' => false,
		'update_post_term_cache' => false
	];

	if (empty($post_args['posts_per_page'])) {
		$default_post_args['no_found_rows'] = true;
	}

	$final_post_args = wp_parse_args($post_args, $default_post_args);

	return new WP_Query($final_post_args);
}

