		];
	}

	$categories = get_the_category();

	if (!empty($categories) && $allow_keys['categories']) {
		$category_html = '';
		// foreach ($categories as $index => $category) {
		// 	if ($index > 0) {
		// 		$category_html .= ', ';
		// 	}

		// 	$category_html .= sprintf(
		// 		'<a class="text-dark text-decoration-none category-item" href="%1$s" title="%2$s">%3$s</a>',
		// 		get_category_link($category),
		// 		esc_attr($category->name),
		// 		$category->name
		// 	);
		// }



		$category_html .= sprintf(
			'<a class="text-dark text-decoration-none category-item" href="%1$s" title="%2$s">%3$s</a>',
			get_category_link($categories['0']),
			esc_attr($categories['0']->name),
			$categories['0']->name
		);

		$items['post_category'] = [
			'icon' => twmp_get_svg_icon('folders'),
			'text' => $category_html
		];
	}

	return $items;
}

function twmp_get_post_date()
{
	$time_string = '<time class="entry-date published text" datetime="%1$s"%5$s>%2$s</time>';

	$updated_time = get_the_modified_time('U');
	$published_time = get_the_time('U') + 1800;

	if ($updated_time > $published_time) {
		if (apply_filters('generate_post_date_show_updated_only', false)) {
			$time_string = '<time class="entry-date updated-date" datetime="%3$s"%6$s>%4$s</time>';
		} else {
			$time_string = '<time class="updated" datetime="%3$s"%6$s>%4$s</time>' . $time_string;
		}
	}

	$time_string = sprintf(
		$time_string,
		esc_attr(get_the_date('c')),
		esc_html(get_the_date()),
		esc_attr(get_the_modified_date('c')),
		esc_html(get_the_modified_date()),
		'',
		''
	);

	$posted_on = '<span class="d-flex align-items-center posted-on">%1$s%4$s</span> ';

	if (apply_filters('generate_post_date_link', false)) {
		$posted_on = '<span class="posted-on">%1$s<a href="%2$s" title="%3$s" rel="bookmark">%4$s</a></span> ';
	}

	echo sprintf(
		$posted_on,
		twmp_get_svg_icon('calendar'),
		esc_url(get_permalink()),
		esc_attr(get_the_time()),
		$time_string
	);
}

function twmp_get_post_author($showIcon = true)
{
	$byline = '<span class="d-flex align-items-center byline">%1$s<span class="author%8$s" %5$s><a class="fn n url" href="%2$s" title="%3$s" rel="author"%6$s><span class="author-name text"%7$s>%4$s</span></a></span></span> ';

	echo sprintf(
		$byline,
		$showIcon ? twmp_get_svg_icon('user') : '<span class="by">' . esc_html__('by', 'twmp-phonghoa') . '</span>',
		esc_url(get_author_posts_url(get_the_author_meta('ID'))),
		/* translators: 1: Author name */
		esc_attr(sprintf(__('View all posts by %s', 'twmp-phonghoa'), get_the_author())),
		esc_html(get_the_author()),
		'',
		'',
		'',
		''
	);
}

function twmp_get_post_categories($showIcon = true)
{
	$term_separator = ',';
	$categories_list = get_the_category_list($term_separator);
	$categories_array = explode($term_separator, $categories_list);
	$first_category_html = $categories_array[0];
	if ($categories_list) {
		echo sprintf(
			'<span class="cat-links">%3$s<span class="screen-reader-text">%1$s </span>%2$s</span> ',
			esc_html_x('Categories', 'Used before category names.', 'twmp-phonghoa'),
			$first_category_html,
			$showIcon ? twmp_get_svg_icon('folders') : '<span class="in">' . esc_html__('in', 'twmp-phonghoa') . '</span>'
		);
	}
}

function twmp_component_pagi_post($args)
{
	if ($args && !empty($args['total'])) :
		$big = 999999999;
		$pagination_links = paginate_links(array(
			'base' => str_replace($big, '%#%', esc_url(get_pagenum_link($big))),
			'format' => '?paged=%#%',
			'current' => max(1, $args['current']),
			'total' => $args['total'],
			'prev_text' => __('&laquo;', 'twmp-phonghoa'),
			'next_text' => __('&raquo;', 'twmp-phonghoa'),
			'type' => 'array',
			'end_size' => 3,
			'mid_size' => 3
		));

		if (is_array($pagination_links)) {
			echo '<ul data-pages="' . $args['total'] . '" class="nav-pagination nav-links text-center d-flex align-items-center justify-content-center">';
			foreach ($pagination_links as $index => $link) {
				if (strpos($link, 'current') !== false) {
					echo '<li class="page-numbers-wrapper">' . str_replace('page-numbers current', 'page-numbers current', $link) . '</li>';
				} else {
					echo '<li class="page-numbers-wrapper">' . str_replace('page-numbers', 'page-numbers', $link) . '</li>';
				}
			}
			echo '</ul>';
		}
	endif;
}
